// 다국어 지원을 위한 유틸리티 함수들
export const getLoveTypeDetail = (typeCode: LoveTypeCode, _t: (key: string) => string): LoveTypeDetail | null => {
    const detail = loveTypeDetails[typeCode];
    if (!detail) return null;

    return detail;
};

export const getLoveTypeDetailField = (typeCode: LoveTypeCode, field: keyof LoveTypeDetail, _t: (key: string) => string): string | null => {
    const detail = loveTypeDetails[typeCode];
    if (!detail) return null;

    return detail[field];
};
